mapper.select:
SELECT COUNT(*)
FROM T_LIKE
WHERE USER_ID = #{userId} AND SPOT_ID = #{spotId}

SELECT LIKE_ID, USER_ID, SPOT_ID
FROM T_LIKE
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM T_LIKE
WHERE SPOT_ID = #{spotId}

mapper.delete:
DELETE FROM T_LIKE
WHERE USER_ID = #{userId}
  AND SPOT_ID = #{spotId}

mapper.select:
SELECT COUNT(*)
FROM T_LIKE
WHERE USER_ID = #{userId}
  AND SPOT_ID = #{spotId}

SELECT COUNT(*)
FROM T_LIKE
WHERE SPOT_ID = #{spotId}
