mapper.select:
SELECT * FROM (
    SELECT A.*, ROWNUM RN
    FROM (
        SELECT NOTICE_ID, ADMIN_ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGDATE, NOTICE_COUNT
        FROM T_NOTICE
        ORDER BY NOTICE_REGDATE DESC
    ) A
    WHERE ROWNUM <= #{startRow} + #{pageSize}
)
WHERE RN > #{startRow}

mapper.update:
UPDATE T_NOTICE
SET NOTICE_COUNT = NOTICE_COUNT + 1
WHERE NOTICE_ID = #{noticeId}
